import React, { useEffect, useState, createRef } from "react";
import { db } from "../firebase-config";
import { doc, setDoc, updateDoc } from "firebase/firestore";
import { collection, getDocs } from "firebase/firestore";
import Card from "react-bootstrap/Card";
import { Col, Row, Form } from "react-bootstrap";
import ScrollContainer from "react-indiana-drag-scroll";
import "../styles/cardStyle.css";

const numbers = new Array(20).fill(1).map((_, index) => index + 1);

const clickHandler = () => {
  alert("clicked!");
};

function BrandProfiles() {
  const [profiles, setProfiles] = useState([]);
  useEffect(() => {
    loop();
  }, []);

  const scrollRef = createRef();
  //#endregion
  let temp = [];
  async function loop() {
    const querySnapshot = await getDocs(collection(db, "Brand"));
    console.log(querySnapshot);
    querySnapshot.forEach((doc) => {
      // doc.data() is never undefined for query doc snapshots
      temp.push(doc.data());
    });
    setProfiles(temp);
  }

  const enableKeyboardCursorToScroll = () => {
    if (scrollRef.current) {
      scrollRef.current.focus();
    }
  };

  return (
    <div>
      <img
        src="gold.jpg"
        width="300px"
        alt="indiana jones contemplates swiping the statue"
      />
      <h1>Drag with Scrollbars using React Indiana Drag Scroll</h1>
      <ScrollContainer className="container">
        <section
          className="tiles"
          onFocus={enableKeyboardCursorToScroll}
          ref={scrollRef}
        >
          {profiles.map((profile) => (
            <Col>
              <Card>
                <Card.Img
                  variant="top"
                  src="https://www.electrochem.org/wp-content/uploads/2019/09/GeorgiaTech400x400.jpg"
                />
                <Card.Body>
                  <Card.Title>{profile.name}</Card.Title>
                  <Card.Text>{profile.description}</Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </section>
      </ScrollContainer>
    </div>
  );
}

export default BrandProfiles;
