import React, { useEffect, useState, createRef, useContext } from "react";
import { db } from "../firebase-config";
import { doc, setDoc, updateDoc, getDoc } from "firebase/firestore";
import { collection, getDocs } from "firebase/firestore";
import Card from "react-bootstrap/Card";
import { Col, Row, Form } from "react-bootstrap";
import ScrollContainer from "react-indiana-drag-scroll";
import "../styles/cardStyle.css";
import { useNavigate } from "react-router-dom";
import {
  ProfileContext,
  SelectedTagsContext,
  ClickedProfileContext,
} from "../App";

const numbers = new Array(20).fill(1).map((_, index) => index + 1);

const clickHandler = () => {
  alert("clicked!");
};

function InfluencerProfiles() {
  const navigate = useNavigate();
  const [profiles, setProfiles] = useState([
    { name: "Avirathuuuu", description: "lolhahaha" },
  ]);
  const { selectedTagList, setSelectedTagList } =
    useContext(SelectedTagsContext);
  const { clickedProfile, setClickedProfile } = useContext(
    ClickedProfileContext
  );
  useEffect(() => {
    if (selectedTagList.length == 0) {
      loop();
    } else {
      console.log("inside selected ue");
      tagEntry();
    }
  }, [selectedTagList]);
  // useEffect(() => {
  //   if (selectedTagList.length == 0) {
  //     loop();
  //   } else {
  //     tagEntry();
  //   }
  //   console.log("ue hello");
  // }, [profiles]);

  const scrollRef = createRef();
  let temp = [];
  async function loop() {
    console.log("if hello");
    const querySnapshot = await getDocs(collection(db, "Influencer"));
    console.log(querySnapshot);
    querySnapshot.forEach((doc) => {
      // doc.data() is never undefined for query doc snapshots
      temp.push(doc.data());
    });
    setProfiles(temp);
  }

  function tagEntry() {
    console.log("hello from lse");
    const profilesSet = new Set();
    selectedTagList.map(async (tag) => {
      const querySnapshot = await getDoc(doc(db, "influencerTags", tag));
      console.log(querySnapshot.data());
      const influencerArr = querySnapshot.data().influencers;
      influencerArr.map((influencer) => {
        console.log(influencer);
        profilesSet.add(influencer);
        console.log(profilesSet);
      });
      temp = [];
      let profilesSetarr = Array.from(profilesSet);
      profilesSetarr.map(async (profile) => {
        const querySnapshot2 = await getDoc(doc(db, "Influencer", profile));
        temp.push(querySnapshot2.data());
      });
      setProfiles(temp);
      console.log(profiles);
    });
  }

  const enableKeyboardCursorToScroll = () => {
    if (scrollRef.current) {
      scrollRef.current.focus();
    }
  };

  function handleClick(profile) {
    setClickedProfile(profile);
    console.log("Hello");
    navigate(`/profiles/${profile.email}`);
  }

  return (
    <div>
      <div>
        <img
          src="gold.jpg"
          width="300px"
          alt="indiana jones contemplates swiping the statue"
        />
        <h1>Drag with Scrollbars using React Indiana Drag Scroll</h1>
        <ScrollContainer className="container">
          <section
            className="tiles"
            onFocus={enableKeyboardCursorToScroll}
            ref={scrollRef}
          >
            {console.log(profiles.slice())}
            {profiles.map((profile) => (
              <Col>
                <div>
                  <Row>
                    <div onClick={() => handleClick(profile)}>
                      <Card style={{ width: "18rem", cursor: "pointer" }}>
                        <Card.Img variant="top" src="holder.js/100px180" />
                        <Card.Body>
                          <Card.Title>{profile.name}</Card.Title>
                          <Card.Text>{profile.description}</Card.Text>
                          <button>View Profile</button>
                        </Card.Body>
                      </Card>
                    </div>
                  </Row>
                </div>
              </Col>
            ))}
          </section>
        </ScrollContainer>
      </div>
    </div>
  );
}

export default InfluencerProfiles;
